import React from 'react';
import axios from 'axios';

class RequestDetails extends React.Component {
  constructor(){
    super();
    this.state = {
      userDatas: [],
      requestDatas: []
    };
  }

/*
  constructor(){
    super();
    this.state = {
      datas:[]
    };
  }


  componentWillMount(){
    const request = axios.create({
      baseURL: 'http://18.178.35.28:3001/'
    })

    request.get(`/users/1`)
    .then(res => {
      this.setState({
        datas: res.data
      });
    })
  }
  */
  componentWillMount(){
    const request = axios.create({
      baseURL: 'http://18.178.35.28:3001/'
    })

    /*request.get(`/users/${this.props.location.userId}/`)
    .then(resUsers => {
      this.setState({
        userDatas: resUsers.data
      });
    })*/
    //this.props.match.params.requestId でURLを取得してる
    request.get(`/requests/${this.props.match.params.requestId}/`)
    .then(resRequests => {
      this.setState({
        requestDatas: resRequests.data
      });
    })
  }



  render() {
    return (
      <div>
        <h1>依頼詳細</h1>
        <p>{this.state.userDatas.u_name}</p>
        <p>{this.state.requestDatas.r_title}</p>
      </div>
    );
  }
}
export default RequestDetails;
