  componentWillMount(){
    const request = axios.create({
      baseURL: 'http://18.178.35.28:3001/'
    })

    //this.props.match.params.requestId でURLを取得してる
    //依頼情報の取得
    request.get(`/requests/${this.props.match.params.requestId}/`)
    .then(resRequests => {
      this.setState({
        requestDatas: resRequests.data
      });
    })

    //依頼ユーザ情報の取得
    request.get(`/users/${this.state.requestDatas.r_u_id}/`)
    .then(resUsers => {
      this.setState({
        userDatas: resUsers.data
      });
    })
  }



  render() {
    console.log(`userDatas : ${this.state.userDatas}`)
    return (
      <div>
        <h1>依頼詳細</h1>
        <p>Name : {this.state.userDatas.u_name}</p>
        <p>Title : {this.state.requestDatas.r_title}</p>
      </div>
    );
  }
}
export default RequestDetails;
