import React from 'react';
import axios from 'axios';

import RequestDetailsUser from './RequestDetailsUser';


//依頼詳細画面
class RequestDetails extends React.Component {
  constructor(){
    super();
    this.state = {
      userDatas: []
    };
  }

/*
