import React from 'react';


//ログイン画面
class Login extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      mail: '',
      password: ''
    } // stateのkey名とフォームのname属性を一致させる
    this.handleChange = this.handleChange.bind(this)
  }

  handleChange (e) {
    let name = e.target.name; // フォームのname属性を取得
    this.setState({ [name]: e.target.value }) // name属性 = stateのkey名なのでstateに保存
  }


  render() {
    return (
      <div>
        <p>titleの値：{this.state.mail}</p>
        <p>textの値：{this.state.password}</p>

        <input value="メールアドレス" name="mail" value={this.state.mail} onChange={this.handleChange} />

        <input value="パスワード" name="password" type="password" onChange={this.handleChange} />
      </div>
    )
  }


}




export default Login;
