import React from 'react';
import axios from 'axios';

import ButtonComponent from '../public/ButtonComponent';

//css
import '../css/submitProposal.css';

//提案入力画面
class SubmitProposal extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      datas:[],
      clothState: []
    }
  }

  componentWillMount(){
    /*const request = axios.create({
      baseURL: 'http://18.178.35.28:3001'
    })*/
    this.setState({
      clothsState: JSON.parse(localStorage.getItem('clothsData'))
    })
  }



  render() {
    return (
      <div>
        {console.log(this.state)}
        <div className="memo">
          <textarea rows="10"  placeholder="メモを入力"></textarea>
        </div>


        <ButtonComponent
          link = {`/completed`}
          buttonText = "提案を決定する"
        />
      </div>
    );
  }
}

export default SubmitProposal;
