import React from 'react';
//import axios from 'axios';

import ButtonComponent from '../public/ButtonComponent';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import ListSubheader from '@material-ui/core/ListSubheader';

import Loading from '../public/Loading';
//css
import '../css/submitProposal.css';

//提案入力画面
class SubmitProposal extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      datas:[],
      clothsData: []
    }
  }

  componentWillMount(){
    /*const request = axios.create({
      baseURL: 'http://18.178.35.28:3001'
    })*/
    this.setState({
      clothsData: JSON.parse(localStorage.getItem('clothsData'))
    })
  }



  render() {
    console.log(this.state.clothsData.c_u_id)
    if(!Number.isInteger(this.state.clothsData.c_u_id)) return <Loading />
    return (
      <React.Fragment>
        {console.log(this.state.clothsData)}
        {this.state.clothsData.map((cloth) => {
          if(cloth.id) {
            return (
              <div className="root">
                <GridList cellHeight={200} className="gridList">
                  <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>
                    <ListSubheader component="div" className="listSubheader" style={{fontSize: '2rem'}}>所有服一覧</ListSubheader>
                  </GridListTile>

                  {/* 提案服一覧表示 */}
                  {cloth.c_link_data.map((cloth) => {
                    return (
                      <GridListTile key={cloth.id} style={{width: '20%'}}>
                        <img src={cloth.c_link} alt={`服画像id : ${cloth.id}`} />
                      </GridListTile>
                    )
                  })}


                  <p>memo: {cloth.p_memo}</p>

                </GridList>
              </div>
            )
          } else return <Loading />
        })}





        <div className="memo">
          <textarea rows="10"  placeholder="メモを入力"></textarea>
        </div>


        <ButtonComponent
          link = {`/completed`}
          buttonText = "提案を決定する"
        />
    </React.Fragment>
    );
  }
}

export default SubmitProposal;
