import React from 'react';
import axios from 'axios';

import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
//import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';
//import IconButton from '@material-ui/core/IconButton';
//import InfoIcon from '@material-ui/icons/Info';

//css
import '../css/clothsTile.css';

/*
const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    width: 500,
    height: 450,
  },
  icon: {
    color: 'rgba(255, 255, 255, 0.54)',
  },
}));
*/





class ClothsTile extends React.Component {
  constructor(){
    super();
    this.state = {
      datas: []
    };
  }

  componentWillMount(){
    const request = axios.create({
      baseURL: 'http://18.178.35.28:3001'
    })

    request.get(`/cloths`)
    .then(res => {
      this.setState({
        datas: res.data
      });
    })
  }




  render() {
    //const classes = useStyles();

    return (
      <div className="root">
      <GridList cellHeight={200} className="gridList">

        <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>
          <ListSubheader component="div" className="listSubheader">所有服一覧</ListSubheader>
        </GridListTile>

        {this.state.datas.map(tile => (
          <GridListTile key={tile.c_link} style={{width: '20%'}}>
            <img src={tile.c_link} alt={`服画像id : ${tile.c_u_id}`} />
            {/*<GridListTileBar
              title="タイトル"
              subtitle={<span>by: {tile.author}</span>}
              actionIcon={
                <IconButton aria-label={`info about`} className={classes.icon}>
                  <InfoIcon />
                </IconButton>
              }
            />*/}
          </GridListTile>
        ))}

      </GridList>
    </div>
    )
  }
}

export default ClothsTile;
