import React from 'react';
import { connect } from 'react-redux';
import { Field, reduxForm } from 'redux-form';
import { Link } from 'react-router-dom';
import RaisedButton from 'material-ui/RaisedButton';
import TextField from 'material-ui/TextField';

//ログイン画面
class Login extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      u_mail: '',
      u_password: ''
    } // stateのkey名とフォームのname属性を一致させる
    this.handleChange = this.handleChange.bind(this)
  }


  renderField(field) {
    const { input, label, type, meta: { touched, error } } = field

    return(
      <TextField
        hintText={label}
        floatingLabelText={label}
        type={type}
        errorText={touched && error}
        {...input}
        fullWidth={true}
      />
    )
  }



  handleChange (e) {
    let name = e.target.name; // フォームのname属性を取得
    this.setState({ [name]: e.target.value }) // name属性 = stateのkey名なのでstateに保存
  }





  render() {
    const { handleSubmit, pristine, submitting } = this.props
    const style = { margin: 12 }

    return (
      <div>
        <h1>ログイン画面</h1>
        <form onSubmit={handleSubmit(this.onSubmit)}>

          <div>
            <Field
              label="メールアドレス"
              name="u_mail"
              type="text"　
              value={this.state.u_name} onChange={this.handleChange} component={this.renderField} />
          </div>

          <div>
            <Field
              label="パスワード"
              name="u_password"
              type="password"
              value={this.state.u_password}
              onChange={this.handleChange}
              component={this.renderField} />
          </div>


          <RaisedButton label="ログイン" type="submit" style={style} disabled={pristine || submitting} />

          <RaisedButton label="新規登録" style={style} containerElement={<Link to="/signIn"  />} />

        </form>
      </div>
    )
  }
}


const validate = values => {
  const errors = {}

  if (!values.u_name) errors.u_name = "Entar a 1title, Please."
  if (!values.u_mail) errors.u_mail = "Entar a 2title, Please."
  if (!values.u_password) errors.u_password = "Entar a 3title, Please."

  return errors
}

//const mapDispatchToProps = ({ signInEvent })

export default connect(null, null)(
  reduxForm({ validate, form: 'loginForm' })(Login)
)
