import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

/* myComponents */
import RequestCard from './RequestCard';



//import img from '../img/iconSample.jpg';

class TimeLine extends React.Component {
  constructor(){
    super();
    this.state = {
      //datas:[]
      requestDatas: [],
      userDatas: []
    };
  }

  /*componentWillMount(){
    const request = axios.create({
      baseURL: 'http://18.178.35.28:3001'
    })

    request.get(`/requests`)
    .then(res => {
      this.setState({
        datas: res.data
      });
    })
  }*/



  //ここからtest
  componentWillMount(){
    const request = axios.create({
      baseURL: 'http://18.178.35.28:3001'
    })

    //this.props.match.params.requestId でURLを取得してる
    //依頼情報の取得
    request.get(`/requests/${this.props.match.params.requestId}`)
    .then(res => {
      this.setState({ requestDatas: res.data })
      this.setUser(request, this.state.requestDatas.r_u_id)
    })
  }

  setUser(request, userId) {
    request.get(`users/${userId}`)
    .then(res => {
      this.setState({ userDatas : res.data})
    })
  }





  render() {
    return (
      <div>
        {this.state.datas.map((hogehoge) => {
          return (
            <Link to={`/request/${hogehoge.id}`}
               key={hogehoge.id}
               className="container"
            >
              <RequestCard
                requestId = {hogehoge.id}
                title = {hogehoge.r_title}
                limit = {hogehoge.r_limit}
                memo = {hogehoge.r_memo}
                userId = {hogehoge.r_u_id}
              />
          </Link>
          )
        })}
      </div>
    );
  }
}

export default TimeLine;
