import React from 'react';
import axios from 'axios';

import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import ButtonBase from '@material-ui/core/ButtonBase';

//css
import '../css/requestCard.css';




class RequestCard extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      userDatas:[]
    }
  }



  componentWillMount(){
    const request = axios.create({
      baseURL: 'http://18.178.35.28:3001'
    })

    if(this.props.userId !== undefined) {
      request.get(`/users/${this.props.userId}`)
      .then(res => {
        this.setState({
          userDatas: res.data
        });
      })
    }
  }


  useStyles = makeStyles(theme => ({
    root: {
      flexGrow: 1,
    },
    paper: {
      padding: theme.spacing(2),
      margin: 'auto',
      maxWidth: 500,
    },
    image: {
      width: 128,
      height: 128,
    },
    img: {
      margin: 'auto',
      display: 'block',
      maxWidth: '100%',
      maxHeight: '100%',
    },
  }));


  render() {
    //const style = { border: this.props.borderStyle }
    const classes = useStyles();
    return (
      <div className={classes.root}>
      <Paper className={classes.paper}>
        <Grid container spacing={2}>
          <Grid item>
            <ButtonBase className={classes.image}>
              <img className={classes.img} alt="user_icon" src={this.state.userDatas.u_icon} />
            </ButtonBase>
          </Grid>
          <Grid item xs={12} sm container>
            <Grid item xs container direction="column" spacing={2}>
              <Grid item xs>
                <Typography gutterBottom variant="subtitle1">
                  {this.props.title}
                </Typography>
                <Typography variant="body2" gutterBottom>
                  {this.props.memo}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  this.props.limit
                </Typography>
              </Grid>
            </Grid>
            {
              (() => {
                if (this.props.bcSelected === false) {
                  return (
                    <Grid item>
                      <Typography variant="subtitle1" className="accent bold">
                      BCを選択してください
                    </Typography>
                    </Grid>
                  )
                }
              })()
            }
          </Grid>
        </Grid>
      </Paper>
    </div>
    );
  }
}

export default RequestCard;
