import React from 'react';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';

class ButtonComponent extends React.Component {

  render() {
    if(this.props.accect !== undefined) {
      const bgColor = {
        backgroundColor:this.props.accect,
        color: 'white',
        height:40,
        fontSize: '2.0rem',
        textAlign: 'center',
        marginTop: 10,
        marginBottom: 10
      }
    } else {
      const button = {
        backgroundColor:'#ff9800',color: 'white',
        height:40,
        fontSize: '2.0rem',
        textAlign: 'center',
        marginTop: 10,
        marginBottom: 10
      }
    }


    return (
          <Link to={`${this.props.link}`} >
            <Button variant="contained" style={this.button} style={this.bgColor}>
              {this.props.buttonText}
            </Button>
          </Link>

    );
  }
}

export default ButtonComponent;
