  renderSelectSloths(selectCloths) {
    const ary = selectCloths.split('_')
    ary.map(function(v) {
      return parseInt(v)
    })


    ary.map((cloths) => {
      return (
        <GridListTile key={cloths.id} style={{width: '20%'}}>
          <img src={`http://18.178.35.28:3001`} alt="proposal image"/>
        </GridListTile>
      )
    })

  }
