  constructor() {
  super();
  this.state = {
    data: [],
    baseURL: 'http://18.178.35.28:3001/'
  }
}

  componentWillMount() {
  const request = axios.create(this.state.baseURL)

  request.get(`/users/${this.props.userId}/`)
  .then(res => {
    this.setState({
      datas: res.data
